import React, { useState } from "react";
import { Link } from "react-router-dom";
import Login from "./Login";

const Main = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const handleLoginSuccess = (username) => {
    setCurrentUser(username);
    setShowLogin(false);
  };

  const handleLoginClick = () => setShowLogin(true);
  const handleCloseLogin = () => setShowLogin(false);

  return (
    <div className="main">
      <nav className="navbar">
        <div className="icon">
          <h2 className="logo">
            <span className="highlight-letter">T</span>
            ravel
          </h2>
        </div>
        <ul className="menu">
          <li>
            <Link to="/">HOME</Link>
          </li>
          <li>
            <Link to="/itinerary">ITINERARY</Link>
          </li>
          <li>
            <Link to="/search">SEARCH & BOOK</Link>
          </li>
          <li>
            <Link to="/services">SERVICES</Link>
          </li>
          <li>
            <Link to="/budget">BUDGET</Link>
          </li>
          <li>
            <a onClick={handleLoginClick}>LOGIN</a>
          </li>
        </ul>
        <div className="search">
          <input
            type="search"
            placeholder="Type to search"
            style={{
              padding: 8,
              borderRadius: 4,
              border: "1px solid #ccc",
              width: 200,
            }}
          />
          <button>Search</button>
        </div>
      </nav>

      <header className="hero-heading">
        <h1>
          Travel Smarter & <spa>Not Harder</spa>
        </h1>
        <p>
          Hi <strong>{currentUser || "Guest"}</strong>, turn your travel dreams
          into reality by effortlessly organizing every detail in one place.
          Juggling dozens of apps and endless spreadsheets? This app unifies
          your entire trip planning experience. Map out your route, manage your
          budget with real-time tracking, receive personalized packing
          recommendations, and share your journey with friends and family
          instantly. Spend less time planning and more time exploring.
        </p>
      </header>

      {showLogin && (
        <Login onLogin={handleLoginSuccess} onClose={handleCloseLogin} />
      )}
    </div>
  );
};

export default Main;
