import React, { useState } from "react";
import { Link } from "react-router-dom";

const Login = ({ onLogin, onClose }) => {
  const [showModal, setShowModal] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState("");

  // Just for demo - logs login attempt
  const handleLogin = async () => {
    console.log("Login button clicked!");
    if (!email || !password) {
      setErrorMsg("Email and Password are required");
      return;
    }

    try {
      const response = await fetch(
        "http://localhost:8090/api/user/auth/login",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ email, password }),
        }
      );

      if (response.ok) {
        const data = await response.text();
        const [token, username] = data.split(":");

        localStorage.setItem("token", token);
        localStorage.setItem("username", username);

        setErrorMsg("");

        if (typeof onLogin === "function") {
          onLogin(username);
        }

        if (typeof onClose === "function") {
          onClose();
        }
      } else {
        const errorMsg = await response.text();
        setErrorMsg(errorMsg || "Login failed");
      }
    } catch (error) {
      console.error("Login Error:", error);
      setErrorMsg("Server error. Please try again later.");
    }
  };

  return (
    <div className={styles.loginPage}>
      <main>
        <form className={styles.loginForm} onSubmit={handleLogin}>
          {errorMsg && <p className={styles.error}>{errorMsg}</p>}

          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
          />

          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            required
            onChange={(e) => setPassword(e.target.value)}
          />

          <Link to="/forgot-password" className={styles.forgotPassword}>
            Forgot Password?
          </Link>

          <button type="submit">Login</button>

          <div className={styles.createAccount}>
            <p>
              Don't have an account? <a href="/register">Create your account</a>
            </p>
          </div>

          <button
            type="button"
            className={styles.closeBtn}
            onClick={onClose}
            style={{ marginTop: "10px" }}
          >
            Close
          </button>
        </form>
      </main>
    </div>
  );
};

const styles = {
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0,0,0,0.5)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontFamily: "inherit", // Keeps font same as parent
    zIndex: 1000,
  },
  modal: {
    backgroundColor: "#fff",
    padding: "30px 40px",
    borderRadius: "8px",
    width: "320px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.3)",
  },
  input: {
    width: "100%",
    padding: "10px",
    marginBottom: "15px",
    fontSize: "16px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    fontFamily: "inherit",
  },
  loginBtn: {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontFamily: "inherit",
  },
  closeBtn: {
    padding: "10px 20px",
    backgroundColor: "#6c757d",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    fontFamily: "inherit",
  },
};

export default Login;
