import React, { useState } from "react";
import { useNavigate,Link } from "react-router-dom";
import styles from "./Login.module.css"; // assuming this exists

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setErrorMsg("Email and Password are required");
      return;
    }

    try {
      const response = await fetch(
        "http://localhost:8090/api/user/auth/login",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ email, password }),
        }
      );

      if (response.ok) {
        const token = await response.text(); // because backend returns plain string
        localStorage.setItem("token", token); // Store the token
        setErrorMsg("");
        navigate("/"); // or navigate("/dashboard");
      } else {
        const errorMsg = await response.text();
        setErrorMsg(errorMsg || "Login failed");
      }
    } catch (error) {
      console.error("Login Error:", error);
      setErrorMsg("Server error. Please try again later.");
    }
  };

  return (
    <div className={styles.loginPage}>
      <div className="menu">
        <ul>
          <li>
            <Link to="/">HOME</Link>
          </li>
          <li>
            <Link to="/itinerary">ITINERARY</Link>
          </li>
          <li>
            <Link to="/search">SEARCH & BOOK</Link>
          </li>
          <li>
            <Link to="/services">SERVICES</Link>
          </li>
          <li>
            <Link to="/budget">BUDGET</Link>
          </li>
        </ul>
      </div>

      <main>
        <form className={styles.loginForm} onSubmit={handleLogin}>
          {errorMsg && <p className={styles.error}>{errorMsg}</p>}

          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
          />

          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            required
            onChange={(e) => setPassword(e.target.value)}
          />

          <a href="/forgot-password" className={styles.forgotPassword}>
            Forgot Password?
          </a>

          <button type="submit">Login</button>

          <div className={styles.createAccount}>
            <p>
              Don't have an account? <a href="/register">Create your account</a>
            </p>
          </div>
        </form>
      </main>
    </div>
  );
};

export default Login;
