import React, { useState } from "react";
import { Link } from "react-router-dom";
import Login from "./Login";
import styles from "./Main.css";

const Main = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const handleLoginSuccess = (username) => {
    console.log("User logged in:", username);
    setCurrentUser(username); // Update username state here
    setShowLogin(false); // Close login modal
  };

  const handleCloseModal = () => {
    setShowLogin(false);
  };

  const handleLoginClick = () => setShowLogin(true);

  return (
    <>
      <div className="main">
        <div className="navbar">
          <div className="icon">
            <h2 className="logo">
              <span className="highlight-letter">T</span>ravel
            </h2>
          </div>
          <div className="menu">
            <ul>
              <li>
                <a href="/">HOME</a>
              </li>
              <li>
                <a href="/itinerary">ITINERARY</a>
              </li>
              <li>
                <a href="/search">SEARCH & BOOK</a>
              </li>
              <li>
                <a href="/services">SERVICES</a>
              </li>
              <li>
                <a href="/budget">BUDGET</a>
              </li>
              <li>
                <a
                  onClick={handleLoginClick}
                  style={{ cursor: "pointer", color: "white" }}
                >
                  LOGIN
                </a>
              </li>
            </ul>
          </div>
          <div className="search">
            <input
              className="srch"
              type="search"
              placeholder="Type to text"
              style={{ color: "white" }}
            />
            <button
              className="btn"
              style={{ color: "white", outline: "white" }}
            >
              search
            </button>
          </div>
          <div className="hero-heading">
            <h1>
              Travel Smarter & <span>Not Harder</span>
            </h1>
            <p className="par">
              Hi {currentUser || "Guest"}, Turn your travel dreams into reality
              by effortlessly organizing every detail in one place. Juggling
              dozens of apps and endless spreadsheets? This app unifies your
              entire trip planning experience. Map out your route, manage your
              budget with real-time tracking, receive personalized packing
              recommendations, and share your journey with friends and family
              instantly. Spend less time planning and more time exploring.
            </p>
          </div>
          {showLogin && (
            <Login onLogin={handleLoginSuccess} onClose={handleCloseModal} />
          )}
        </div>
      </div>
    </>
  );
};

export default Main;
