import React, { useState } from "react";
import { Link } from "react-router-dom";
import Login from "./Login";

const Main = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const handleLoginSuccess = (username) => {
    setCurrentUser(username);
  };

  const handleCloseModal = () => {
    setShowLogin(false);
  };

  const handleLoginClick = () => setShowLogin(true);

  return (
    <>
      <div className="main">
        <div className="navbar">
          <div className="icon">
            <h2 className="logo">
              <span className="highlight-letter">T</span>ravel
            </h2>
          </div>
          <div className="menu">
            <ul>
              <li>
                <Link to="/">HOME</Link>
              </li>
              <li>
                <Link to="/itinerary">ITINERARY</Link>
              </li>
              <li>
                <Link to="/search">SEARCH & BOOK</Link>
              </li>
              <li>
                <Link to="/services">SERVICES</Link>
              </li>
              <li>
                <Link to="/budget">BUDGET</Link>
              </li>
              <li>
                <a onClick={handleLoginClick} style={{ cursor: "pointer" }}>
                  LOGIN
                </a>
              </li>
            </ul>
          </div>
          <div className="search">
            <input className="srch" type="search" placeholder="Type to text" />
            <button className="btn">search</button>
          </div>
          <div className="hero-heading">
            <h1>
              Travel Smarter & <span>Not Harder</span>
            </h1>
            <pre className="par">
              Hi {currentUser || "Guest"}, Turn your travel dreams into reality
              by effortlessly organizing every detail in one place. Juggling
              dozens of apps and endless spreadsheets? This app unifies your
              entire trip planning experience. Map out your route, manage your
              budget with real-time tracking, receive personalized packing
              recommendations, and share your journey with friends and family
              instantly. Spend less time planning and more time exploring.
            </pre>
          </div>
          {showLogin && (
            <Login onLogin={handleLoginSuccess} onClose={handleCloseModal} />
          )}
        </div>
      </div>
    </>
  );
};

export default Main;
