// ItineraryPlanner.jsx
import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';

const Itinerary = () => {
  const [tripName, setTripName] = useState('');
  const [destination, setDestination] = useState('');
  const [days, setDays] = useState([]);

  const addDay = () => {
    setDays([
      ...days,
      {
        name: `Day ${days.length + 1}`,
        date: '',
        locations: [],
        notes: ''
      }
    ]);
  };

  const updateDay = (index, field, value) => {
    const newDays = [...days];
    newDays[index][field] = value;
    setDays(newDays);
  };

  const addLocationToDay = async (dayIndex, searchQuery) => {
    try {
      const res = await axios.get('https://nominatim.openstreetmap.org/search', {
        params: {
          q: `${searchQuery}, ${destination}`,
          format: 'json',
          limit: 1,
        },
      });
      const loc = res.data[0];
      if (loc) {
        const newDays = [...days];
        newDays[dayIndex].locations.push({
          name: searchQuery,
          lat: parseFloat(loc.lat),
          lon: parseFloat(loc.lon),
          time: '',
          note: '',
        });
        setDays(newDays);
      } else {
        alert("Location not found");
      }
    } catch (err) {
      console.error(err);
    }
  };

  const removeLocation = (dayIndex, locIndex) => {
    const newDays = [...days];
    newDays[dayIndex].locations.splice(locIndex, 1);
    setDays(newDays);
  };

  const removeDay = (dayIndex) => {
    const newDays = [...days];
    newDays.splice(dayIndex, 1);
    setDays(newDays);
  };

  const updateLocation = (dayIndex, locIndex, field, value) => {
    const newDays = [...days];
    newDays[dayIndex].locations[locIndex][field] = value;
    setDays(newDays);
  };

  const colors = ['primary', 'success', 'warning', 'info', 'danger'];

  return (
    <div className="container py-4">
      <h2 className="text-center mb-4 text-primary">‚úàÔ∏è Trip Itinerary Planner</h2>

      <div className="row mb-4">
        <div className="col-md-6">
          <label>Trip Name *</label>
          <input
            className="form-control"
            value={tripName}
            onChange={(e) => setTripName(e.target.value)}
            required
            placeholder="My Adventure in Europe"
          />
        </div>
        <div className="col-md-6">
          <label>Destination *</label>
          <input
            className="form-control"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            required
            placeholder="e.g., Bangalore, Paris"
          />
        </div>
      </div>

      <div className="mb-3 text-end">
        <button className="btn btn-success" onClick={addDay}>
          + Add Day
        </button>
      </div>

      {days.map((day, index) => (
        <div
          className={`card border-${colors[index % colors.length]} mb-4 shadow`}
          key={index}
        >
          <div className={`card-header bg-${colors[index % colors.length]} text-white`}>
            <div className="d-flex justify-content-between align-items-center">
              <input
                className="form-control me-2"
                value={day.name}
                onChange={(e) => updateDay(index, 'name', e.target.value)}
                style={{ maxWidth: '50%' }}
              />
              <input
                type="date"
                className="form-control"
                value={day.date}
                onChange={(e) => updateDay(index, 'date', e.target.value)}
                style={{ maxWidth: '40%' }}
              />
              <button
                className="btn btn-sm btn-danger ms-2"
                onClick={() => removeDay(index)}
              >
                üóë
              </button>
            </div>
          </div>

          <div className="card-body">
            {/* Add location input */}
            <div className="input-group mb-3">
              <input
                className="form-control"
                placeholder="Add location (e.g., Lalbagh Garden)"
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault();
                    addLocationToDay(index, e.target.value);
                    e.target.value = '';
                  }
                }}
              />
              <span className="input-group-text">‚Üµ</span>
            </div>

            {/* List of locations with map */}
            {day.locations.map((loc, locIndex) => (
              <div
                key={locIndex}
                className="mb-3 p-3 border rounded bg-light position-relative"
              >
                <div className="row">
                  <div className="col-md-4">
                    <strong>{loc.name}</strong>
                    <div className="mt-1">
                      <small>
                        üìç {loc.lat.toFixed(4)}, {loc.lon.toFixed(4)}
                      </small>
                    </div>
                  </div>
                  <div className="col-md-4">
                    <input
                      type="time"
                      className="form-control"
                      value={loc.time}
                      onChange={(e) =>
                        updateLocation(index, locIndex, 'time', e.target.value)
                      }
                      placeholder="Visit time"
                    />
                  </div>
                  <div className="col-md-3">
                    <input
                      type="text"
                      className="form-control"
                      value={loc.note}
                      onChange={(e) =>
                        updateLocation(index, locIndex, 'note', e.target.value)
                      }
                      placeholder="Note (e.g., must try food)"
                    />
                  </div>
                  <div className="col-md-1 text-end">
                    <button
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => removeLocation(index, locIndex)}
                    >
                      ‚ùå
                    </button>
                  </div>
                </div>
                {/* Leaflet Map */}
                <MapContainer
                  center={[loc.lat, loc.lon]}
                  zoom={14}
                  scrollWheelZoom={false}
                  style={{ height: '200px', marginTop: '10px' }}
                >
                  <TileLayer
                    attribution='&copy; <a href="https://osm.org">OpenStreetMap</a>'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                  />
                  <Marker position={[loc.lat, loc.lon]} />
                </MapContainer>
              </div>
            ))}

            {/* Day Notes */}
            <div>
              <label>üìù Notes for the Day:</label>
              <textarea
                className="form-control"
                rows={2}
                value={day.notes}
                onChange={(e) => updateDay(index, 'notes', e.target.value)}
              />
            </div>
          </div>
        </div>
      ))}

      <div className="text-center">
        <button
          className="btn btn-primary btn-lg"
          onClick={() => console.log('Trip Data:', { tripName, destination, days })}
        >
          ‚úÖ Save Trip
        </button>
      </div>
    </div>
  );
};

export default Itinerary;
